import React, { useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import './Booking.css';

export default function Booking() {
  const location = useLocation();
  const roomData = location.state?.room;

  const today = new Date();
  const tomorrow = new Date();
  tomorrow.setDate(today.getDate() + 1);

  const formatDate = (date) => date.toISOString().split('T')[0];

  const [userId, setUserId] = useState(''); // Fetch this from auth in a real app
  const [roomId, setRoomId] = useState(roomData?._id || '');
  const [arrival, setArrival] = useState(formatDate(today));
  const [departure, setDeparture] = useState(formatDate(tomorrow));
  const [noOfPerson, setNoOfPerson] = useState(1);
  const [message, setMessage] = useState('');

  const handleArrivalChange = (e) => {
    const newArrival = e.target.value;
    setArrival(newArrival);
    if (new Date(departure) <= new Date(newArrival)) {
      const newDep = new Date(newArrival);
      newDep.setDate(newDep.getDate() + 1);
      setDeparture(formatDate(newDep));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3007/api/booking', {
        user: userId,
        room: roomId,
        arrival,
        departure,
        no_of_person: noOfPerson,
      });
      setMessage('✅ Booking successful!');
    } catch (err) {
      setMessage(err.response?.data?.msg || 'Something went wrong.');
    }
  };

  return (
    <div className="booking-container">
      <div className="booking-card">
        <h3 className="booking-heading">🛏️ Booking Form</h3>

        {roomData && (
          <div className="room-details">
            <h5>Room Details</h5>
            <p><strong>Type:</strong> {roomData.room_type}</p>
            <p><strong>Number:</strong> {roomData.room_number}</p>
            <p><strong>Price:</strong> ₹{roomData.price}</p>
            <p><strong>Status:</strong> {roomData.is_available ? '✅ Available' : '❌ Not Available'}</p>
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="row">
            <div className="col-md-6 mb-3">
              <label className="booking-label">User ID</label>
              <input type="text" className="form-control booking-input" value={userId} readOnly />
            </div>

            <div className="col-md-6 mb-3">
              <label className="booking-label">Room ID</label>
              <input type="text" className="form-control booking-input" value={roomId} readOnly />
            </div>

            <div className="col-md-6 mb-3">
              <label className="booking-label">Arrival Date</label>
              <input type="date" className="form-control booking-input" value={arrival} min={formatDate(today)} onChange={handleArrivalChange} required />
            </div>

            <div className="col-md-6 mb-3">
              <label className="booking-label">Departure Date</label>
              <input type="date" className="form-control booking-input" value={departure} min={arrival} onChange={(e) => setDeparture(e.target.value)} required />
            </div>

            <div className="col-md-12 mb-3">
              <label className="booking-label">No. of Persons</label>
              <input type="number" className="form-control booking-input" value={noOfPerson} min="1" onChange={(e) => setNoOfPerson(e.target.value)} required />
            </div>

            <div className="col-md-12">
              <button className="btn btn-danger w-100" type="submit">
                🔒 Book Now
              </button>
            </div>
          </div>
        </form>

        {message && (
          <div className="alert alert-info mt-3 text-center booking-message">
            {message}
          </div>
        )}
      </div>
    </div>
  );
}
