import React, { useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

export default function Booking() {
  const location = useLocation();
  const roomData = location.state?.room;

  const today = new Date();
  const tomorrow = new Date();
  tomorrow.setDate(today.getDate() + 1);

  const formatDate = (date) => date.toISOString().split('T')[0];

  const [userId, setuserId] = useState('');
  const [roomId, setRoomId] = useState(roomData?._id || '');
  const [arrival, setArrival] = useState(formatDate(today));
  const [departure, setDeparture] = useState(formatDate(tomorrow));
  const [noOfPerson, setNoOfPerson] = useState(1);
  const [message, setMessage] = useState('');

  const handleArrivalChange = (e) => {
    const newArrival = e.target.value;
    setArrival(newArrival);
    if (new Date(departure) <= new Date(newArrival)) {
      const newDep = new Date(newArrival);
      newDep.setDate(newDep.getDate() + 1);
      setDeparture(formatDate(newDep));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3007/booking/create', {
        user: userId,
        room: roomId,
        arrival,
        departure,
        no_of_person: noOfPerson,
      });
      setMessage('‚úÖ Booking successful!');
    } catch (err) {
      setMessage(err.response?.data?.msg || 'Something went wrong.');
    }
  };

  const labelStyle = { color: 'white', fontWeight: 'bold' };
  const inputStyle = { backgroundColor: '#222', color: 'white', border: '1px solid red' };
  const cardStyle = { backgroundColor: '#111', color: 'white', border: '1px solid red' };
  const headingStyle = { color: 'red' };

  return (
    <div className="container mt-5">
      <div className="card shadow p-4" style={cardStyle}>
        <h3 className="mb-4 text-center" style={headingStyle}>üõèÔ∏è Booking Form</h3>

        {roomData && (
          <div className="mb-4 p-3 border rounded" style={{ backgroundColor: '#1a1a1a', borderColor: 'red' }}>
            <h5 style={headingStyle}>Room Details</h5>
            <p><strong>Type:</strong> {roomData.room_type}</p>
            <p><strong>Number:</strong> {roomData.room_number}</p>
            <p><strong>Price:</strong> ‚Çπ{roomData.price}</p>
            <p><strong>Status:</strong> {roomData.is_available ? '‚úÖ Available' : '‚ùå Not Available'}</p>
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="row">
            <div className="col-md-6 mb-3">
              <label style={labelStyle}>User ID</label>
              <input
                type="text"
                className="form-control"
                style={inputStyle}
                value={userId}
                readOnly
              />
            </div>

            <div className="col-md-6 mb-3">
              <label style={labelStyle}>Room ID</label>
              <input
                type="text"
                className="form-control"
                style={inputStyle}
                value={roomId}
                readOnly
              />
            </div>

            <div className="col-md-6 mb-3">
              <label style={labelStyle}>Arrival Date</label>
              <input
                type="date"
                className="form-control"
                style={inputStyle}
                value={arrival}
                min={formatDate(today)}
                onChange={handleArrivalChange}
                required
              />
            </div>

            <div className="col-md-6 mb-3">
              <label style={labelStyle}>Departure Date</label>
              <input
                type="date"
                className="form-control"
                style={inputStyle}
                value={departure}
                min={arrival}
                onChange={(e) => setDeparture(e.target.value)}
                required
              />
            </div>

            <div className="col-md-12 mb-3">
              <label style={labelStyle}>No. of Persons</label>
              <input
                type="number"
                className="form-control"
                style={inputStyle}
                value={noOfPerson}
                min="1"
                onChange={(e) => setNoOfPerson(e.target.value)}
                required
              />
            </div>

            <div className="col-md-12">
              <button className="btn btn-danger w-100" type="submit">
                üîí Book Now
              </button>
            </div>
          </div>
        </form>

        {message && (
          <div className="mt-3 alert alert-info text-center" style={{ backgroundColor: '#222', color: 'white', borderColor: 'red' }}>
            {message}
          </div>
        )}
      </div>
    </div>
  );
}
