import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

export default function Booking() {
  const location = useLocation();
  const roomData = location.state?.room;

  const today = new Date();
  const tomorrow = new Date();
  tomorrow.setDate(today.getDate() + 1);

  const formatDate = (date) => date.toISOString().split('T')[0];

  const [userId, setUserId] = useState('');
  const [roomId, setRoomId] = useState(roomData?._id || '');
  const [arrival, setArrival] = useState(formatDate(today));
  const [departure, setDeparture] = useState(formatDate(tomorrow));
  const [noOfPerson, setNoOfPerson] = useState(1);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    setUserId('user123');
  }, []);

  const handleArrivalChange = (e) => {
    const newArrival = e.target.value;
    setArrival(newArrival);

    if (new Date(departure) <= new Date(newArrival)) {
      const newDeparture = new Date(newArrival);
      newDeparture.setDate(newDeparture.getDate() + 1);
      setDeparture(formatDate(newDeparture));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setError('');

    try {
      await axios.post('http://localhost:3007/booking/create', {
        user: userId,
        room: roomId,
        arrival,
        departure,
        no_of_person: noOfPerson,
      });
      setMessage('‚úÖ Booking successful!');
    } catch (err) {
      const msg = err.response?.data?.msg || 'Something went wrong.';
      setError(`‚ùå ${msg}`);
    }
  };

  return (
    <div className="container py-5" style={{ backgroundColor: '#121212', minHeight: '100vh' }}>
      <div
        className="card p-4 shadow-lg mx-auto"
        style={{
          maxWidth: '800px',
          borderRadius: '16px',
          backgroundColor: '#1f1f1f',
          color: '#fff',
          border: '1px solid #ff4b5c',
        }}
      >
        <h3 className="text-center mb-4" style={{ color: '#ff4b5c' }}>
          <i className="bi bi-calendar-check-fill me-2"></i>
          Book Your Room
        </h3>

        {roomData && (
          <div className="mb-4 p-3 border border-danger bg-black rounded">
            <h5 className="text-danger"><i className="bi bi-door-open-fill me-2"></i>Room Details</h5>
            <ul className="list-unstyled mb-0">
              <li><strong>Type:</strong> {roomData.room_type}</li>
              <li><strong>Number:</strong> {roomData.room_number}</li>
              <li><strong>Price:</strong> ‚Çπ{roomData.price}</li>
              <li>
                <strong>Status:</strong>{' '}
                <span className={`badge ${roomData.is_available ? 'bg-success' : 'bg-danger'}`}>
                  {roomData.is_available ? 'Available' : 'Not Available'}
                </span>
              </li>
            </ul>
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="row g-3">
            <div className="col-md-6">
              <label className="form-label text-light">üë§ User ID</label>
              <input type="text" className="form-control bg-dark text-white border-danger" value={userId} readOnly />
            </div>

            <div className="col-md-6">
              <label className="form-label text-light">üè† Room ID</label>
              <input type="text" className="form-control bg-dark text-white border-danger" value={roomId} readOnly />
            </div>

            <div className="col-md-6">
              <label className="form-label text-light">üìÖ Arrival Date</label>
              <input
                type="date"
                className="form-control bg-dark text-white border-danger"
                value={arrival}
                min={formatDate(today)}
                onChange={handleArrivalChange}
                required
              />
            </div>

            <div className="col-md-6">
              <label className="form-label text-light">üìÜ Departure Date</label>
              <input
                type="date"
                className="form-control bg-dark text-white border-danger"
                value={departure}
                min={arrival}
                onChange={(e) => setDeparture(e.target.value)}
                required
              />
            </div>

            <div className="col-12">
              <label className="form-label text-light">üßç No. of Persons</label>
              <input
                type="number"
                className="form-control bg-dark text-white border-danger"
                value={noOfPerson}
                min="1"
                onChange={(e) => setNoOfPerson(e.target.value)}
                required
              />
            </div>

            <div className="col-12">
              <button
                type="submit"
                className="btn w-100 fw-bold text-white"
                style={{
                  background: 'linear-gradient(to right, #ff4b5c, #c70039)',
                  padding: '12px',
                  fontSize: '1.1rem',
                  border: 'none',
                }}
              >
                <i className="bi bi-check2-circle me-2"></i>Book Now
              </button>
            </div>
          </div>
        </form>

        {message && (
          <div className="alert alert-success mt-4 text-center fw-semibold">{message}</div>
        )}

        {error && (
          <div className="alert alert-danger mt-4 text-center fw-semibold">{error}</div>
        )}
      </div>
    </div>
  );
}
