import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

export default function Booking() {
  const location = useLocation();
  const roomData = location.state?.room;

  const today = new Date();
  const tomorrow = new Date();
  tomorrow.setDate(today.getDate() + 1);

  const formatDate = (date) => date.toISOString().split('T')[0];

  const [userId, setUserId] = useState('');
  const [roomId, setRoomId] = useState(roomData?._id || '');
  const [arrival, setArrival] = useState(formatDate(today));
  const [departure, setDeparture] = useState(formatDate(tomorrow));
  const [noOfPerson, setNoOfPerson] = useState(1);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    // Simulated user ID (replace with actual logic)
    setUserId('user123');
  }, []);

  const handleArrivalChange = (e) => {
    const newArrival = e.target.value;
    setArrival(newArrival);

    if (new Date(departure) <= new Date(newArrival)) {
      const newDeparture = new Date(newArrival);
      newDeparture.setDate(newDeparture.getDate() + 1);
      setDeparture(formatDate(newDeparture));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setError('');

    try {
      await axios.post('http://localhost:3007/booking/create', {
        user: userId,
        room: roomId,
        arrival,
        departure,
        no_of_person: noOfPerson,
      });
      setMessage('‚úÖ Booking successful!');
    } catch (err) {
      const msg = err.response?.data?.msg || 'Something went wrong.';
      setError(`‚ùå ${msg}`);
    }
  };

  return (
    <div className="container mt-5">
      <div className="card shadow-lg border-0 p-4" style={{ background: 'linear-gradient(to right, #fdfbfb, #ebedee)' }}>
        <h3 className="text-center text-primary mb-4" style={{ fontWeight: 'bold' }}>
          üéâ Book Your Stay
        </h3>

        {roomData && (
          <div className="mb-4 p-3 bg-info bg-opacity-10 border border-info rounded">
            <h5 className="text-info">Room Details</h5>
            <p><strong>Type:</strong> {roomData.room_type}</p>
            <p><strong>Number:</strong> {roomData.room_number}</p>
            <p><strong>Price:</strong> ‚Çπ{roomData.price}</p>
            <p><strong>Status:</strong> 
              <span className={`badge ms-2 ${roomData.is_available ? 'bg-success' : 'bg-danger'}`}>
                {roomData.is_available ? 'Available' : 'Not Available'}
              </span>
            </p>
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="row">
            <div className="col-md-6 mb-3">
              <label className="form-label text-secondary">User ID</label>
              <input
                type="text"
                className="form-control bg-light"
                value={userId}
                readOnly
              />
            </div>

            <div className="col-md-6 mb-3">
              <label className="form-label text-secondary">Room ID</label>
              <input
                type="text"
                className="form-control bg-light"
                value={roomId}
                readOnly
              />
            </div>

            <div className="col-md-6 mb-3">
              <label className="form-label text-secondary">Arrival Date</label>
              <input
                type="date"
                className="form-control"
                value={arrival}
                min={formatDate(today)}
                onChange={handleArrivalChange}
                required
              />
            </div>

            <div className="col-md-6 mb-3">
              <label className="form-label text-secondary">Departure Date</label>
              <input
                type="date"
                className="form-control"
                value={departure}
                min={arrival}
                onChange={(e) => setDeparture(e.target.value)}
                required
              />
            </div>

            <div className="col-md-12 mb-3">
              <label className="form-label text-secondary">No. of Persons</label>
              <input
                type="number"
                className="form-control"
                value={noOfPerson}
                min="1"
                onChange={(e) => setNoOfPerson(e.target.value)}
                required
              />
            </div>

            <div className="col-md-12">
              <button className="btn btn-gradient-primary w-100 fw-bold" type="submit" style={{
                background: 'linear-gradient(to right, #ff416c, #ff4b2b)',
                color: '#fff',
                border: 'none',
                padding: '10px',
              }}>
                üíº Book Now
              </button>
            </div>
          </div>
        </form>

        {message && (
          <div className="mt-4 alert alert-success text-center fw-semibold">
            {message}
          </div>
        )}

        {error && (
          <div className="mt-4 alert alert-danger text-center fw-semibold">
            {error}
          </div>
        )}
      </div>
    </div>
  );
}
